DROP TABLE IF EXISTS teams, players;

-- teams
CREATE TABLE teams (
    id SERIAL,
    name VARCHAR(255) PRIMARY KEY,
    logo text
);



-- players
CREATE TABLE players (
    id SERIAL PRIMARY KEY,
    playerName VARCHAR(255),
    team text REFERENCES teams(name),
    position text,
    years text,
    mpg text,
    fgPct text,
    threePtPct text,
    ftPct text,
    ppg text,
    rpg text,
    apg text,
    bpg text,
    stls text,
    plusMinus text,
    headImg text,
    restImg text,
    winImg text,
    loseImg text,
    points text
    
);

